mapper.select:
SELECT 
	DT, INSTRUMENT_CD, VALUE_TYPE, LEG_TYPE, PERIOD_NUM, VALUE1, VALUE2
FROM 
	PROC_DETAIL_DATA
WHERE 
	INSTRUMENT_CD = #{instrumentCd}
	AND PROC_ID = #{procId}
	AND IDX = #{idx}
	AND VALUE_TYPE = #{valueType}
	AND NONCALL_CD = #{nonCallCd}


    	AND LEG_TYPE = #{legType}

SELECT 
	DT, INSTRUMENT_CD, VALUE_TYPE, ISNONCALL, LEG_TYPE, PERIOD_NUM, VALUE1, VALUE2
FROM 
	PROC_DETAIL_DATA
WHERE 
	INSTRUMENT_CD = #{instrumentCd}
	AND PROC_ID = #{procId}
	AND IDX = #{idx}
	AND VALUE_TYPE = #{valueType}
	AND ISNONCALL = #{isNonCall}
	AND LEG_TYPE = #{legType}
