mapper.select:
SELECT 
	DT, INSTRUMENT_CD, PRICE, PAY_PRICE, RCV_PRICE, CCY_CD
FROM 
	PROC_PRICE_DATA
WHERE
	PROC_ID = #{procId}
	AND IDX = #{idxId}
	AND NONCALL_CD = #{nonCallCd}

SELECT 
	DT, INSTRUMENT_CD, PRICE, PAY_PRICE, RCV_PRICE, CCY_CD
FROM 
	PROC_PRICE_DATA    
WHERE 
	PROC_ID = #{procId}
	AND IDX = #{idxId}
	AND NONCALL_CD = #{nonCallCd}
	AND INSTRUMENT_CD = #{instrumentCd}
	AND DT > #{startDate}
	AND DT < #{endDate}
